import React, { useState, useEffect } from 'react';
import { getAll } from '../../services/database';
import { useSelector} from 'react-redux';

import './MyBooksFriends.scss'

import Layout from '../../Layout';
import CardBook from '../../components/CardBook/CardBook';


const MyFriendsBooks = () => {

    const user = useSelector((state)=> state.user);

    const [bookList, setBookList] = useState([])
    console.log('bookList: ', bookList);

    const list = bookList.filter(function(elem){
        return elem.id !== user;
    })

    console.log('list: ', list);



    useEffect(() => {

        const result = getAll('books')
        setBookList(result)

      }, [user])

    return ( 
        <Layout>
            <div className="cardBook-container">
            {bookList.map((book, i)=>(
                <CardBook book={book} />
            ))}
            </div>
        </Layout>
     );
}

export default MyFriendsBooks;
