import API from './callAPI2';

const BOOK_API_BASE = 'https://www.googleapis.com/books/v1/volumes?q=isbn:';

class BookApi {

  static async bookISBNSearch( codeISBN ) {
    const httpApi = new API(BOOK_API_BASE);

    const books = await httpApi.get(
      '',
      { 
        numISBN: codeISBN, 
      });
    return books
  }
}

export default BookApi;
