import React , { useState, useEffect } from 'react'

const MoviesList = () => {
    const [movieList, setMovieList] = useState([]);
    const keyAPI = 'fa3015aa3c35912635c7d973c01a9364'

    useEffect(()=>{
        fetch(`https://api.themoviedb.org/3/search/company?api_key=${keyAPI}&query=sss&page=1`)
        .then(function(response) {
            return response.json();
          })
          .then(function(movieList) {
            setMovieList(movieList.results)
            console.log('setMovieList: ', setMovieList);


          });

          console.log(movieList) //aqui me salen los resultados

        // .then(movieList =>{
        //     setMovieList(movieList)
        // }
        // )
    })


    return ( 
        //aqui NO me salen los resultados
        <div>
            {movieList.map(movieItem=> (
             <div key={movieItem.id}>
             <h2>{movieItem.original_title}</h2>
            </div>))}
        </div> );
}

export default MoviesList;






