import React, { useState, useEffect } from 'react';


const AddBookButton = ({bookISBNSearch}) => {


    const [searchISBN, setSearchISBN] = useState()
    const [bookItem, setBookItem] = useState([])

    const onInputChange = (event)=>{
        setSearchISBN(event.target.value)
    }

    const showBook = (event)=>{
        event.preventDefault();
        bookISBNSearch(searchISBN); //falta declarar aquesta funció
        ; 
    }

    bookISBNSearch = (codeISBN) => {
        console.log('codeISBN: ', codeISBN);

        useEffect((codeISBN) => {
            fetch(`https://www.googleapis.com/books/v1/volumes?q=isbn:${codeISBN}`)
              .then(results => results.json())
              .then(data => {
                const {bookItem} = data.results[0];
                setBookItem(bookItem.title);
              });
          }, []); // <-- Have to pass in [] here!
    }

    return ( 
        <form className="searchBook" onSubmit={showBook}>
        <input placeholder="type ISBN number" value={searchISBN} onChange={onInputChange} />
        <button type="submit">SEARCH</button>
        <button>ADD BOOK</button>

        </form>
     );
}

export default AddBookButton;
