const INITIAL_STATE = 0

const counterReducer = (state = INITIAL_STATE, action) => {
    const counter = state;

    switch(action.type){
        case 'INCREMENT': {
            return counter +1
        }
        case 'DECREMENT':{
            return counter > 0 ? counter -1 : counter
        }
        default :{
            return counter
        }
    }
}

export default counterReducer
