import React, {useState, useEffect} from 'react';

import { getAllRealTime } from '../../services/database';



const SearchBook = () => {

    const [searchBookItem, setSearchBookiItem]= useState('')
    console.log('searchBookItem: ', searchBookItem);
    console.log('setSearchBookiItem: ', setSearchBookiItem);
    const [searchResult, setSearchResult] = useState([])
    console.log('searchResult: ', searchResult);
    console.log('setSearchResult: ', setSearchResult);
    const [bookList, setBookList] = useState([])
    console.log('bookList: ', bookList);
    console.log('setBookList: ', setBookList);


    const handleSearch = (event) =>{setSearchBookiItem(event.target.value)};

    useEffect(() => {

        getAllRealTime({
            collection: 'books',
            filters: { field: 'title', condition: 'array-contains', value: searchBookItem },
            order: 'timestamp',
            callback: (collectionData) => {
            const results = [];
            collectionData.forEach((document) => {
                const data = document.data();
                const addedBookDate = new Date(data.timestamp);
                data.date = addedBookDate.toLocaleDateString();
                data.time = addedBookDate.toLocaleTimeString();
                results.push(data);


            });
            setBookList(results);
            }
        });
      }, [])


    useEffect (() => {
        const results = bookList.filter(bookItem => bookItem.toLowerCase().includes(searchBookItem));
        setSearchResult(results);
    }, [searchBookItem]);


    return (
        <>
        <div>
            <input type="text" placeholder="search a book" value={searchBookItem} onChange={handleSearch}/>
        </div>
        {searchBookItem !== undefined ?  <div></div> :
            <ul>
                {searchResult.map(item => (
                <li>{item}</li>))}
            </ul> 
    }
        </>
    )
}

export default SearchBook;
