import React, { useState } from "react";


const SearchBook = ({ bookList, setBookList }) => {

  const [searchInput, setSearchInput] = useState('');

  const handleChange = word => {
      setSearchInput(word);
  }

  const handleClick = () =>{

    const filteredList = bookList.filter(item => {
      if (item) {
        return item.authors.includes(searchInput) || item.title.includes(searchInput)
      }
      return false
    })
    setBookList(filteredList)
  }

  return (
      <div className="searchBook">
          <input placeholder="search book" type="text" value={searchInput} onChange={(e) => handleChange(e.target.value)} />
          <button className="search-button" type="submit" onClick={handleClick}>SEARCH</button>
      </div>
    )
}

export default SearchBook;
