import React, {useState, useEffect} from 'react';

const bookListName = [
    "Hola",
    "hola HOLA",
    "adeu",
    "asdfsdf",
    "ASDadasdf"
];

const SearchBook = () => {

    const [searchBookItem, setSearchBookiItem]= useState('')
    const [searchResult, setSearchResult] = useState([])

    const handleSearch = (event) =>{setSearchBookiItem(event.target.value)};

    const results = (() => {
        bookListName.filter(bookItem =>bookItem.toLowerCase().includes(searchBookItem.toLowerCase()));
        setSearchResult(results);
    }, [searchResult]);


    return (
        <>
        <div>
            <input type="text" placeholder="search a book" value={searchBookItem} onChange={handleSearch}/>
        </div>
        <ul>
            {searchResult.map(item => (
            <li>{item}</li>
            ))}
        </ul>
        </>
    )
}

export default SearchBook;
