import React , { useState, useEffect } from 'react'

const MoviesList = () => {
    const [movieList, setMovieList] = useState([]);
    const keyAPI = 'fa3015aa3c35912635c7d973c01a9364'

    useEffect(()=>{
        fetch('https://api.themoviedb.org/3/search/company?api_key=fa3015aa3c35912635c7d973c01a9364&query=sss&page=1')
        .then(data => data.json())
        .then(movieList =>{
            setMovieList(movieList)
        })
    })

    return ( <div>
        {movieList}
        {/* { <div>
              {movieList.map(movieItem=> (
             <div key={movieItem.id}>
             <h2>{movieItem.original_title}</h2>
               </div>))}
        </div>} */}

    </div> );
}

export default MoviesList;



