import React, { useState, useEffect }from 'react';


const MoviesList = () => {
    const [movieList, setMovieList] = useState([]);
    const keyAPI = 'fa3015aa3c35912635c7d973c01a9364'

    useEffect(() => {
    fetch(`https://api.themoviedb.org/3/discover/movie?api_key=${keyAPI}&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&vote_average.gte=10`)
      .then(response => response.json())
      .then(movieList => setMovieList(movieList));
  }, []);

    return ( 
        <div>
            {movieList.map(movieItem=> (
                <div key={movieItem.id}>
                <h2>{movieItem.original_title}</h2>
                </div>))}
        </div>
     );
}

export default MoviesList;

