import React , { useState, useEffect } from 'react';
import axios from 'axios';

// const codeISBN = '9788482649665'
// const BookISBNSearch = axios.get(`https://www.googleapis.com/books/v1/volumes?q=isbn:${codeISBN}`)
//   .then(function (response) {
//     const beerInitial = response.data;
//     console.log('beerInitial: ', beerInitial);


//   }
// )

const BookISBNSearch = () => {
    const [bookItem, setBookItem] = useState([]);
    const codeISBN = '9788482649665'

    useEffect(()=>{
        fetch(`https://www.googleapis.com/books/v1/volumes?q=isbn:${codeISBN}`)
        .then(function(response) {
            console.log('response: ', response);
            return response.json();
        })
        .then(data => {
            const aa = setBookItem(data.items[0].volumeInfo)
            console.log('aa: ', aa);


        });
        console.log(JSON.stringify(setBookItem, null, 2));
    }, [])

    if (!bookItem) return ("error")


    return ( 
        <div>
             <h3>{bookItem.title}</h3>   
             <h2>{bookItem.author}</h2>
             <h2>{bookItem.description}</h2>
        </div>
        );
}

export default BookISBNSearch;
