import React, { useState } from 'react';
import BookISBNSearch from '../AddBookButton/AddBookButton'

const SearchISBN = () => {

    const [searchedISBN, setSearchedISBN] = useState()


    const onInputChange = (event)=>{
        setSearchedISBN(event.target.value)
        console.log('searchedISBN: ', searchedISBN);

    }

    const showBook = (event)=>{
        event.preventDefault();
        BookISBNSearch(searchedISBN);
        console.log('searchISBN2: ', searchedISBN);
        ; 
    }

    return ( 
        <form className="searchBook" onSubmit={showBook}>
            <input placeholder="type ISBN number" value={searchedISBN} onChange={onInputChange} />
            <button type="submit">SEARCH</button>
            <button>ADD BOOK</button>
        </form>
     );
}

export default SearchISBN;
