import React, {useState} from 'react';
import {
  BrowserRouter as Router,
  Switch,

} from "react-router-dom";

import Home from './pages/Home';
import About from './pages/About';
import MyBookList from './components/MyBookList';
import Header from './components/Header/Header';
import SearchISBN from './components/SearchISBN/SearchISBN';
import BookISBNSearch from './components/AddBookButton/AddBookButton';

function App() {

  const [valueISBN, setValueISBN]= useState('');

  const handleISBNvalue = (ISBNvalue)=>{
    setValueISBN(ISBNvalue)
  }

  return (
    <>
      <Router>
        <Header />
        <Switch>
          <Router path="/Home"><Home /></Router>
          <Router path="/About"><About /></Router>
        </Switch>
      </Router>
    <div>
      <div>HookTheBook - 9788482649665</div>
      <div><SearchISBN onPush={handleISBNvalue}/></div>
      <div><BookISBNSearch /></div>
      <div><MyBookList /></div>
    </div>
    </>
  )
}

export default App;
