import React, { useState, useEffect } from "react";


const SearchBook = ({ bookList, setBookList }) => {

  const [searchInput, setSearchInput] = useState('');

  const handleChange = word => {
      setSearchInput(word);
  }

  const handleClick = () =>{

    const filteredList = bookList.filter(item =>{
      console.log('item: ', item);
      if (item){
        return item.authors.includes(searchInput) || item.title.includes(searchInput)
      }
    })
    setBookList(filteredList)
    console.log('filteredList: ', filteredList);

  }

//   useEffect (()=>{

